// we want to leverage the behavior of the endpoint /auth
type TestServer struct {
	httpServer   *httptest.Server
	tokenHandler *MockTokenHandler
	jwksHandler  *MockJWKsHandler

        /* Create handler interface in file test/utils/oidc/handlers.go
        generate its mock implementation and inject it into the server */
        authHandler  *MockAuthHandler

	TearDownFn   func()
}

// Create handler's getter to be able to have access and adjust handler in a calling code
func (ts *TestServer) AuthHandler() *MockAuthHandler {
	return ts.authHandler
}

func BuildAndRunTestServer(t *testing.T, caPath, caKeyPath string) *TestServer {    
...  
        // Change auth HTTP handler
	mux.HandleFunc(authWebPath, func(writer http.ResponseWriter, request *http.Request) {  
		result, err := oidcServer.authHandler.Authenticate()
		if err != nil {
			http.Error(writer, err.Error(), http.StatusBadRequest)

			return
		}

		writer.Header().Add("Content-Type", "application/json")
		writer.WriteHeader(http.StatusOK)

		err = json.NewEncoder(writer).Encode(result)
		require.NoError(t, err)

		return 
